INSERT INTO `source_code` VALUES (83, 1084, '#include <cstdio>\n#include <cstring>\n#include <cstdlib>\n#include <sys/socket.h>\n#include <sys/unistd.h>\n#include <sys/types.h>\n#include <sys/errno.h>\n#include <netinet/in.h>\n#include <signal.h>\n#define BUFFSIZE 2048\n#define DEFAULT_PORT 16555    // 指定端口为16555\n#define MAXLINK 2048\nint sockfd, connfd;    // 定义服务端套接字和客户端套接字\nvoid stopServerRunning(int p)\n{\n    close(sockfd);\n    printf(\"Close Server\n\");\n    exit(0);\n}\nint main()\n{\n    struct sockaddr_in servaddr;    // 用于存放ip和端口的结构\n    char buff[BUFFSIZE];    // 用于收发数据\n    // 对应伪代码中的sockfd = socket();\n    sockfd = socket(AF_INET, SOCK_STREAM, 0);\n    if (-1 == sockfd)\n    {\n        printf(\"Create socket error(%d): %s\n\", errno, strerror(errno));\n        return -1;\n    }\n    // END\n    // 对应伪代码中的bind(sockfd, ip::port和一些配置);\n    bzero(&servaddr, sizeof(servaddr));\n    servaddr.sin_family = AF_INET;\n    servaddr.sin_addr.s_addr = htonl(INADDR_ANY);\n    servaddr.sin_port = htons(DEFAULT_PORT);\n    if (-1 == bind(sockfd, (struct sockaddr*)&servaddr, sizeof(servaddr)))\n    {\n        printf(\"Bind error(%d): %s\n\", errno, strerror(errno));\n        return -1;\n    }\n    // END\n    // 对应伪代码中的listen(sockfd);    \n    if (-1 == listen(sockfd, MAXLINK))\n    {\n        printf(\"Listen error(%d): %s\n\", errno, strerror(errno));\n        return -1;\n    }\n    // END\n    printf(\"Listening...\n\");\n    while (true)\n    {\n        signal(SIGINT, stopServerRunning);    // 这句用于在输入Ctrl+C的时候关闭服务器\n        // 对应伪代码中的connfd = accept(sockfd);\n        connfd = accept(sockfd, NULL, NULL);\n        if (-1 == connfd)\n        {\n            printf(\"Accept error(%d): %s\n\", errno, strerror(errno));\n            return -1;\n        }\n        // END\n        bzero(buff, BUFFSIZE);\n        // 对应伪代码中的recv(connfd, buff);\n        recv(connfd, buff, BUFFSIZE - 1, 0);\n        // END\n        printf(\"Recv: %s\n\", buff);\n        // 对应伪代码中的send(connfd, buff);\n        send(connfd, buff, strlen(buff), 0);\n        // END\n        // 对应伪代码中的close(connfd);\n        close(connfd);\n        // END\n    }\n    return 0;\n}');
INSERT INTO `source_code` VALUES (84, 1085, '#include <cstdio>\n#include <cstring>\n#include <cstdlib>\n#include <sys/socket.h>\n#include <sys/unistd.h>\n#include <sys/types.h>\n#include <sys/errno.h>\n#include <netinet/in.h>\n#include <arpa/inet.h>\n#define BUFFSIZE 2048\n#define SERVER_IP \"192.168.19.12\"    // 指定服务端的IP，记得修改为你的服务端所在的ip\n#define SERVER_PORT 16555            // 指定服务端的port\nint main()\n{\n    struct sockaddr_in servaddr;\n    char buff[BUFFSIZE];\n    int sockfd;\n    sockfd = socket(AF_INET, SOCK_STREAM, 0);\n    if(-1 == sockfd)\n    {\n        printf(\"Create socket error(%d): %s\n\", errno, strerror(errno));\n        return -1;\n    }\n    bzero(&servaddr, sizeof(servaddr));\n    servaddr.sin_family = AF_INET;\n    inet_pton(AF_INET, SERVER_IP, &servaddr.sin_addr));\n    servaddr.sin_port = htons(SERVER_PORT);\n    if (-1 == connect(sockfd, (struct sockaddr*)&servaddr, sizeof(servaddr)))\n    {\n        printf(\"Connect error(%d): %s\n\", errno, strerror(errno));\n        return -1;\n    }\n    printf(\"Please input: \");\n    scanf(\"%s\", buff);\n    send(sockfd, buff, strlen(buff), 0);\n    bzero(buff, sizeof(buff));\n    recv(sockfd, buff, BUFFSIZE - 1, 0);\n    printf(\"Recv: %s\n\", buff);\n    close(sockfd);\n    return 0;\n}');
INSERT INTO `source_code` VALUES (85, 1086, '#include <stdio.h>\n#include <sys/socket.h>\n#include <netinet/in.h>  \n#include <netdb.h>  \n#include <fcntl.h>  \n#include <unistd.h>  \n#include <stdlib.h>\n#include <sys/stat.h>  \n#include <sys/types.h>  \n#include <arpa/inet.h>\n#include <time.h>\n#include <iostream>\n#include <string.h>\n#include <stdint.h>\n#include <csignal>\n#include <iostream>\n#include <assert.h>\n#include <math.h>\n\nusing namespace std;\n\n/////////////////////////////////////////////////////////\nconst float CPU_freq = 2.194844;\n// const float CPU_freq = 2.808002;\n\ntypedef struct timestamp {\n    uint32_t coreId;\n    uint64_t value;\n} timestamp_t;\n\ninline void read_timestamp_counter(timestamp_t * t) {\n    uint64_t highTick, lowTick;\n    asm volatile (\"rdtscp\" : \"=d\"(highTick), \"=a\"(lowTick), \"=c\"(t->coreId));\n    t->value = highTick << 32 | lowTick;\n}\n\ninline uint64_t\ndiff_timestamps(const timestamp_t * before,\n                const timestamp_t * after) {\n    assert(before->coreId == after->coreId);\n    return after->value - before->value;\n}\n\ninline uint64_t\ncycle_since_timestamp(const timestamp_t * previous) {\n    timestamp_t now;\n    read_timestamp_counter(&now);\n    return diff_timestamps(previous, &now);\n}\n\nuint64_t get_time(){\n	struct timespec tv;\n    clock_gettime(CLOCK_REALTIME,&tv);\n    \n	uint64_t clock = (tv.tv_sec % 1000) * 1e9 + tv.tv_nsec;\n	\n	return clock;\n}\n/////////////////////////////////////////////////////////\n\ntypedef unsigned int SOCKET;\n#define INVALID_SOCKET  -1  \n#define SOCKET_ERROR    -1  \n\n#define BUFFER_SZ 1200\n#define SEND_BUF_LEN 512\n\nint server_port = 33456;\nint client_port = 33457;\n\nconst char* ServerIP = \"127.0.0.1\";\n\nint Send_num = 200000;\n\nconst int RTT_LEN = 1000000;\nuint64_t *rtt = new uint64_t[RTT_LEN];\n\nint mode = -1;\n\n// void sig_handler(int sig) {\n//     if ( sig == SIGINT){\n//         // sender\n//         if(mode == 0){\n            \n//         }\n//         // receiver\n//         else{\n            \n//         }\n//         delete []rtt;\n//         close(m_sock);\n//     }\n//     exit(0);\n// }\n\n\nint main(int argc, char *argv[]){\n    if(argc != 2){\n        std::cout << \"Usage: APP NAME, MODE(0:Servver, 1:Client)\" << std::endl;\n    }\n    mode = atoi(argv[1]);\n\n    /*Server: pong*/\n    /*Client: ping*/\n\n    //注册中断信号\n    // signal( SIGINT, sig_handler );\n\n    ///Server\n    if(mode == 0){\n        SOCKET m_sock;\n        char buffer[BUFFER_SZ];\n        //Create a socket\n        if((m_sock = socket(AF_INET , SOCK_DGRAM , IPPROTO_UDP )) == INVALID_SOCKET)\n        {\n            return 0;\n        }\n        sockaddr_in my_addr;\n        sockaddr_in remote_addr; //客户端网络地址结构体  \n        memset(&my_addr,0,sizeof(sockaddr_in));\n        //Prepare the sockaddr_in structure\n        my_addr.sin_family = AF_INET;\n        my_addr.sin_addr.s_addr = htonl(INADDR_ANY);\n        my_addr.sin_port = htons( server_port );\n\n        //Bind\n        if( bind(m_sock ,(sockaddr *)&my_addr , sizeof(sockaddr)) == SOCKET_ERROR)\n        {\n            printf(\"Socket bind failed\n\");\n            close(m_sock);\n            return 0;\n        }\n\n        int sin_size=sizeof(struct sockaddr_in);  \n\n        cout << \"waiting for packets...\" << endl;\n        for(int i=0; i < Send_num; i++)\n        {\n            // do_pong\n            int nRecEcho = recvfrom(m_sock, (char*)buffer, BUFFER_SZ , 0, (struct sockaddr *)&remote_addr, (socklen_t *)&sin_size);  \n            if (nRecEcho < 0)  \n            {  \n                printf(\"[SERVER]recv error/n\");  \n                break;  \n            }\n            else if (nRecEcho != 512){\n                printf(\"invalid packet received!\n\");\n            }\n            \n            int len = sendto(m_sock, buffer, nRecEcho, 0, (struct sockaddr *)&remote_addr, sizeof(struct sockaddr));\n            if(len == -1){\n                std::cout << \"[SERVER]send failed!\" << std::endl;\n            }\n        }\n        close(m_sock);\n        cout << \"complete!\" << endl;\n    }\n    ///Client\n    else{\n        SOCKET m_sock;\n        char buffer[SEND_BUF_LEN];\n\n        if((m_sock = socket(AF_INET , SOCK_DGRAM , IPPROTO_UDP )) == INVALID_SOCKET)\n        {\n            return 0;\n        }\n        sockaddr_in remote_addr;\n        memset(&remote_addr,0,sizeof(sockaddr_in));\n        //Prepare the sockaddr_in structure\n        remote_addr.sin_family = AF_INET;\n        remote_addr.sin_addr.s_addr = inet_addr(ServerIP);//服务器IP地址  \n        remote_addr.sin_port = htons( server_port );\n\n\n        sockaddr_in my_addr;\n        memset(&my_addr,0,sizeof(sockaddr_in));\n        //Prepare the sockaddr_in structure\n        my_addr.sin_family = AF_INET;\n        my_addr.sin_addr.s_addr = htonl(INADDR_ANY);\n        my_addr.sin_port = htons( client_port );\n\n        //Bind\n        if( bind(m_sock ,(sockaddr *)&my_addr , sizeof(sockaddr)) == SOCKET_ERROR)\n        {\n            printf(\"Socket bind failed\n\");\n            close(m_sock);\n            return 0;\n        }\n\n        memset(buffer, 36, SEND_BUF_LEN);\n\n        int sin_size=sizeof(struct sockaddr_in);  \n\n        // do_ping\n        for(int i=0; i < Send_num; i++){\n            timestamp_t start;\n            read_timestamp_counter(&start);\n\n            buffer[0] = 0xff;\n            buffer[1] = 0xee;\n            buffer[2] = 0x00;\n            buffer[3] = 0x02;//little endien\n            memcpy(buffer + 110, &i, sizeof(i));\n            memcpy(buffer + 50, &(start), sizeof(start));\n            // int len = sendto(m_sock, buffer, sizeof(i), 0, NULL, 0);\n            // std::cout << \"sending: \" << i << std::endl;\n            int len = sendto(m_sock, buffer, SEND_BUF_LEN, 0, (struct sockaddr *)&remote_addr, sizeof(struct sockaddr));\n            if(len == -1){\n                std::cout << \"socket send failed!\" << std::endl;\n            }\n\n            int nRecEcho = recvfrom(m_sock, (char*)buffer, BUFFER_SZ , 0, (struct sockaddr *)&remote_addr, (socklen_t *)&sin_size);  \n            if (nRecEcho < 0)  \n            {  \n                printf(\"[CLIENT]recv error/n\");  \n                break;  \n            }\n            else if (nRecEcho != 512){\n                printf(\"[CLIENT]invalid packet received!\n\");\n            }\n\n            uint64_t latency = cycle_since_timestamp(&start);\n            rtt[i] = latency / CPU_freq / 2;\n        }\n        close(m_sock);\n\n        uint64_t min = 99999, max = 0, sum = 0;\n        for(int i=0; i < Send_num; i++){\n            // fout << rtt[i] << endl;\n            if (rtt[i] < min){\n                min = rtt[i];\n            }\n            if (rtt[i] > max){\n                max = rtt[i];\n            }\n            sum += rtt[i];\n        }\n        double avg = (double)sum / Send_num;\n        double std = 0.0;\n        double tmp = 0.0;    \n        for(int i=0; i < Send_num; i++){\n            tmp += (rtt[i] - avg) * (rtt[i] - avg);\n        }\n        std = sqrt(tmp/Send_num);\n\n        cout << \"num: \" << Send_num << endl;\n        cout << \"avg: \" << sum / Send_num << endl;\n        cout << \"min: \" << min << endl;\n        cout << \"max: \" << max << endl;\n        cout << \"std: \" << std << endl;\n\n        cout << \"total receive valid packets: \" << Send_num << endl;\n    }\n\n    return 0;\n}');
